    frappe.utils.has_role("System Manager", user=frappe.session.user) or 
    frappe.utils.has_role("Full Admin", user=frappe.session.user)
):
    frappe.throw("You Are Not Allowed To Create Consultant")

if frappe.db.get_value("Journal Entry", {"sales_invoice": doc.name}, "name"):
    stock_entry= frappe.get_doc("Journal Entry",frappe.db.get_value("Journal Entry", {"sales_invoice": doc.name}, "name"))
    if stock_entry.docstatus==1:
        stock_entry.cancel()

