que.insert(ignore_permissions = 1)

number = frappe.form_dict.mobile_no
# frappe.errprint(number)
patien_exists = frappe.db.exists("Patient" ,{"mobile_no" : number} )
if patien_exists:

    frappe.response['message'] = {"number":number , "status" : 200}
else:
    # frappe.errprint(frappe.response)

    frappe.response['http_status_code'] = 401
    frappe.response['message'] = {"msg":"Mobile Number Not Found"}

if doc.consumable_items:
	items = []
	for item in doc.consumable_items:
			items.append({
				"item_code" : item.item_code,
				"qty" : item.qty,

			})
	frappe.errprint(items)
	stock_ent = frappe.get_doc({
		"doctype" : "Stock Entry",
		"posting_date" : frappe.utils.nowdate(),
		"stock_entry_type": "Material Issue",
		"company" : doc.company,
		"from_warehouse": doc.source_warehouse,
		"items" : items,
		})
	stock_ent.insert(ignore_permissions = True)
	stock_ent.submit()
	# doc.db_set("status", "Completed")
	# return stock_ent

if doc.procedure_items:
	pro_items = []
	for item in doc.procedure_items:
			pro_items.append({
				"item_code" : item.item_code,
				"qty" : item.qty
			})
	customer_to = frappe.db.get_value("Patient", doc.patient, "customer")
	sales_doc = frappe.get_doc({
		"doctype" : "Sales Invoice",
		"posting_date" : frappe.utils.nowdate(),
		"customer": customer_to,
		"patient" : doc.patient,
		"is_pos" : 0,
		"items" : pro_items,
		})
	sales_doc.insert(ignore_permissions = True)
	sales_doc.submit()
# 	doc.db_set("status", "Completed")
	# return sales_doc

for i in doc.items:
    if i.item_group == "Dental":
        obs = frappe.get_doc({
            'doctype': 'Dental Result',
            'patient': doc.patient,
            'practitioner': doc.ref_practitioner,
		    "examination":   i.item_code
        })
        obs.insert(ignore_permissions = True)

for i in doc.items:
    # frappe.msgprint(i.item_code)
    if frappe.db.exists("Clinical Procedure Template", i.item_code, cache=True):

        clinical = frappe.get_doc({
            'doctype': 'Pre Anesthesia Evaluation and Re Evaluation Form',
            'pid': doc.patient,
            # 'patient_name': doc.patient_name,
            'surgical_procedure': i.item_code,
            # 'practitioner': doc.ref_practitioner,
            # "company" : doc.company,
            # "appointment_date" : doc.posting_date
        })
        clinical.insert(ignore_permissions = True)

# Ensure payment doesn't exceed grand total unless it's a return
if doc.paid_amount > doc.grand_total and not doc.is_return:
    if doc.payments:
        doc.payments[0].amount = doc.grand_total 
        doc.paid_amount = doc.grand_total

# Handle insurance logic
if doc.is_pos and not doc.is_return:
    if doc.insurance_policy:
        # Only override if values are inconsistent
        expected_amount = doc.payable_amount or 0.0
        if doc.payments and doc.payments[0].amount != expected_amount:
            doc.payments[0].amount = expected_amount
        if doc.paid_amount != expected_amount:
            doc.paid_amount = expected_amount

mode_of_payment=''
pos_profile=''
default_account = ''
if doc.is_pos and doc.is_return and not doc.voucher_no:
    pos_profile= frappe.db.get_value("POS Profile User", {"default": 1, "user": frappe.session.user}, "parent")
    if pos_profile:
        mode_of_payment = frappe.db.get_value("POS Payment Method",{"parent": pos_profile}, "mode_of_payment") 
    if mode_of_payment:

        default_account = frappe.db.get_value('Mode of Payment Account', {"parent": mode_of_payment},  'default_account')
    if default_account:
        doc.payments[0].mode_of_payment= mode_of_payment
        doc.payments[0].account =default_account
    doc.payments[0].amount= doc.grand_total



if doc.is_return and not doc.voucher_no:
    for i in doc.items:
        i.reference_dn=""
        i.reference_dt=""
        i.sales_invoice_item =""
        i.so_detail =""
        i.sales_order =""

if doc.services_so:
    sales_order_service = frappe.get_doc("Sales Order", doc.services_so)
    if sales_order_service.status == "Completed":
        frappe.throw("Service Orders already Billed so it can't change!!!")
if  doc.medication_so:
    sales_order_medication = frappe.get_doc("Sales Order", doc.medication_so)

    if sales_order_medication.status == "Completed":
        frappe.throw("Pharmacy Orders already Billed so it can't change!!!")

if doc.items:
    for item in doc.items:
        if item.rate == 0:
            frappe.throw(_(f"Rate cannot be zero for item: {item.item_code} (Row: {item.idx})"))

if doc.insurance_policy and doc.coverage_limits == 0:
    frappe.throw("The Insurance coverage can not be zero!!!")

if doc.insurance_company and not doc.is_return:
    cust= frappe.get_doc("Customer", doc.customer)
    if cust.credit_limits:
        for i in cust.credit_limits:
            i.credit_limit= i.credit_limit + doc.grand_total
    cust.save()

paid_amount = doc.paid_amount or 0

if doc.paid_amount > doc.grand_total and not doc.is_return: 
    frappe.throw("Paid Amount can not be greater than the grand total!!")

if doc.insurance_company:
    cust= frappe.get_doc("Customer", doc.customer)
    if cust.credit_limits:
        for i in cust.credit_limits:
            i.credit_limit= i.credit_limit - doc.grand_total
    cust.save()

